formulas.expression:
ADDMONTHS(
	DATE(
		YEAR( {!recordId.COMM_EndDate__c} ),
		MONTH( {!recordId.COMM_EndDate__c} ),
		1
	),
	IF( {!COMM_Variable_NewSelectedPeriodicity.periodicity} = 'Monthly',
		2,
		IF( {!COMM_Variable_NewSelectedPeriodicity.periodicity} = 'Quarterly',
			4,
			IF( {!COMM_Variable_NewSelectedPeriodicity.periodicity} = 'Half Yearly',
				7,
				IF( {!COMM_Variable_NewSelectedPeriodicity.periodicity} = 'Yearly',
					13,
					1
				)
			)
		)
	)
)  - 1
