ValidationRule.errorConditionFormula:
OR(
    AND(
        ISPICKVAL(COMM_PreferredContact__c, 'Phone'),
        ISBLANK(Phone)
    ),
    AND(
        ISPICKVAL(COMM_PreferredContact__c, 'E-Mail'),
        OR(
            RecordType.Name = 'Individual' && ISBLANK(PersonEmail),
            RecordType.Name = 'Collective/Partner' && ISBLANK(COMM_Email__c)
        )
    )
)
