ValidationRule.errorConditionFormula:
AND(
    ISPICKVAL(Product2.Family, "Parking"),
    OR(
        AND(
            NOT(ISBLANK(COMM_SecondPeriodStartDay__c)),
            OR(
                ISBLANK(COMM_SecondPeriodEndDay__c),
                ISBLANK(COMM_SecondPeriodDailyMaximum__c)
            )
        ),
        AND(
            NOT(ISBLANK(COMM_SecondPeriodEndDay__c)),
            OR(
                ISBLANK(COMM_SecondPeriodStartDay__c),
                ISBLANK(COMM_SecondPeriodDailyMaximum__c)
            )
        ),
        AND(
            NOT(ISBLANK(COMM_SecondPeriodDailyMaximum__c)),
            OR(
                ISBLANK(COMM_SecondPeriodStartDay__c),
                ISBLANK(COMM_SecondPeriodEndDay__c)
            )
        )
    )
)
