ValidationRule.errorConditionFormula:
AND(
    ISPICKVAL(Product2.Family, "Parking"),
    OR(
        AND(
            NOT(ISBLANK(COMM_ThirdPeriodStartDay__c)),
            OR(
                ISBLANK(COMM_ThirdPeriodEndDay__c),
                ISBLANK(COMM_ThirdPeriodDailyMaximum__c)
            )
        ),
        AND(
            NOT(ISBLANK(COMM_ThirdPeriodEndDay__c)),
            OR(
                ISBLANK(COMM_ThirdPeriodStartDay__c),
                ISBLANK(COMM_ThirdPeriodDailyMaximum__c)
            )
        ),
        AND(
            NOT(ISBLANK(COMM_ThirdPeriodDailyMaximum__c)),
            OR(
                ISBLANK(COMM_ThirdPeriodStartDay__c),
                ISBLANK(COMM_ThirdPeriodEndDay__c)
            )
        )
    )
)
